package com.es.precios.application.response;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.*;import java.io.Serializable;import java.math.BigDecimal;import java.time.LocalDateTime;@Builder@AllArgsConstructor@NoArgsConstructor@JsonIgnoreProperties(ignoreUnknown = true)public class PriceResponse implements Serializable {    private static final long serialVersionUID = -6334581547411113963L;    @JsonProperty("productId")    private Long productId;    @JsonProperty("brandId")    private Integer brandId;    @JsonProperty("price")    private BigDecimal price;    private double tax;    @JsonProperty("finalPrice")    private BigDecimal finalPrice;    @JsonProperty("dateToFound")    private LocalDateTime dateToFound;    @Override    public String toString() {        return "{" +                "productId: "+ productId +                " brandId: "+ brandId +                " price: "+ price +                " priceFinal: "+ finalPrice +                " dateToFound: "+ dateToFound +                "}";    }}